						}
					} else {
						$fieldNames[$tabl['name']]['type'] = 'input';
					}
				break;
				case "enum":
					$fieldNames[$tabl['name']]['type'] = 'select';
					$fieldNames[$tabl['name']]['options'] = array();
					$enumValues = split(',', $fieldLength);

					foreach($enumValues as $enum) {
