/**
 * funcion cambia formato fecha.
 * $fecha= Fecha a cambiar
 * $tipo_return= formato como quieres que la devuelva
 * ej. ("02/08/1984","A-M-D") retorna => 1984-02-08
 *     ("1984-02-08","D-M-A") retorna => 02/08/1984
 */
function Cfecha($fecha,$tipo_return){
	//$paso1 = explode('/', $fecha);
	//$paso2 = explode('-', $fecha);

$paso_aux = explode('-', $fecha);
if(strlen($paso_aux[0])=="4"){

$fecha_return = $fecha;

}else{

      if($tipo_return=="A-M-D"){
           $paso = explode('/', $fecha);
           $fecha_aux[] = $paso[2];
           $fecha_aux[] = $paso[1];
           $fecha_aux[] = $paso[0];
           $fecha_return=implode('-', $fecha_aux);
      }else if($tipo_return=="D/M/A"){
           $paso = explode('-', $fecha);
           $fecha_aux[] = $paso[2];
           $fecha_aux[] = $paso[1];
           $fecha_aux[] = $paso[0];
           $fecha_return=implode('/', $fecha_aux);
     }

}//fin else


//echo $fecha_return;

//$fecha_return = $fecha;

     return $fecha_return;
}//fin Cfecha



