 function dateAdd($dias_adelanto=null,$dia=null,$mes=null,$anio=null){
      if($mes==null){$mes = date("m");}
      if($anio==null){$anio = date("Y");}
      if($dia==null){$dia = date("d");}
      $ultimo_dia = date( "d", mktime(0, 0, 0, $mes + 1, 0, $anio) ) ;
      //$dias_adelanto = $dias;
      $siguiente = $dia + $dias_adelanto;
      if ($ultimo_dia < $siguiente)
      {
         $dia_final = $siguiente - $ultimo_dia;
         $mes++;
         if ($mes == '13')
         {
            $anio++;
            $mes = '01';
         }
         $fecha_final = $this->AddCeroR2($dia_final).'/'.$this->AddCeroR2($mes).'/'.$anio;
      }
      else
      {
         $fecha_final = $this->AddCeroR2($siguiente) .'/'.$this->AddCeroR2($mes).'/'.$anio;
      }
      return $fecha_final;
   }//fin function





