		if($cugd05_restriccion_clave->findCount($cond)==0){
			return false;

		}else{
			return true;
		}
	}else{
		return true;
	}


}else{ // ENVIA SET TRUE O FALSE Y MSJ


	if($_SESSION["Modulo"]!='0'){

        $this->Session->delete('autor_valido');
