		if($cugd05_restriccion_clave->findCount($cond)==0){
			$programa = $this->msj_verifica_entrada($cod_tipo);
			$this->redirect('/administradors/vacio/'.$programa);
		}else{
			$this->set('autor_valido',true);
			$this->set('validado',true);

			$this->Session->write('validado', true);
			$this->Session->write('autor_valido',true);
		}
	}else{
		    $this->set('autor_valido',true);
			$this->set('validado',true);

			$this->Session->write('validado', true);
			$this->Session->write('autor_valido',true);
		}

	}
}


